const WeatherCard = ({ weatherData }) => {
  if (!weatherData) {
    return <p className="text-center mt-8">No weather data available.</p>;
  }

  const name = weatherData?.name || "Unknown Location";
  const temp = weatherData?.main?.temp || "N/A";
  const humidity = weatherData?.main?.humidity || "N/A";
  const description = weatherData?.weather?.[0]?.description || "No description available";
  const iconUrl = weatherData?.weather?.[0]?.icon
    ? `https://openweathermap.org/img/wn/${weatherData.weather[0].icon}@2x.png`
    : null;

  return (
    <div className="max-w-md mx-auto mt-8 bg-white shadow-lg rounded-lg p-6">
      <h2 className="text-2xl font-bold text-center">{name}</h2>
      {iconUrl && (
        <div className="text-center">
          <img src={iconUrl} alt="Weather Icon" className="mx-auto" />
        </div>
      )}
      <p className="text-center text-gray-600 capitalize">{description}</p>
      <div className="flex justify-between mt-4">
        <p className="text-xl">Temp: {temp}Â°C</p>
        <p className="text-xl">Humidity: {humidity}%</p>
      </div>
    </div>
  );
};

export default WeatherCard;
